HOST='127.0.0.1'
PORT=8485

s=socket.socket(socket.AF_INET,socket.SOCK_STREAM)
s.bind((HOST,PORT))
s.listen(10)
conn,addr=s.accept()
image = b''
while True:
    piece = conn.recv(1024)
    if not piece:
        break
    image += piece
with open(r"C:\Users\ken.shay\Desktop\ken.png",'wb') as f:
    f.write(image)
