import numpy as np
import matplotlib.pyplot as plt

N = 4
ind = np.arange(N)


width = 0.27

fig = plt.figure()
ax = fig.add_subplot(111)

Fail_Values = [1, 1, 1,4]
rects1 = ax.bar(ind, Fail_Values, width, color='r')
Pass_Values = [2,2,2,6]
rects2 = ax.bar(ind+width,Pass_Values, width, color='g')



ax.set_ylabel('Fail / Pass')
ax.set_xticks(ind+width)
