def delete_file(service, file_id):
  """Permanently delete a file, skipping the trash.

  Args:
    service: Drive API service instance.
    file_id: ID of the file to delete.
  """
  try:
    drive_service.files().delete(fileId=file_id).execute()
  except Exception as error:
    print('An error occurred: %s' % error)


def createFolder(name):
    file_metadata = {
    'name': name,
    'mimeType': 'application/vnd.google-apps.folder'
    }
    file = drive_service.files().create(body=file_metadata,
                                        fields='id').execute()
    print ('Folder ID: %s' % file.get('id'))



def update_permission(file_id, permission_id, new_role):

  try:
    # First retrieve the permission from the API.
    permission = drive_service.permissions().get(
        fileId=file_id, permissionId=permission_id).execute()
    permission['role'] = new_role
    return drive_service.permissions().update(
        fileId=file_id, permissionId=permission_id, body=permission).execute()
  except Exception as e:
    print('An error occurred: %s' % e)
  return None

def update_permission_for_all_files():
    items = list_Files()
    for i in items:
        permission_id = 'https://www.googleapis.com/auth/drive'
        new_role = 'owner'
        update_permission(i['id'],permission_id,new_role)





def retrieve_permissions_for_all_files():
    items = list_Files()
    for i in items:
        perm = retrieve_permissions(i['id'])
        print(perm)



def retrieve_permissions(file_id):
  try:
    permissions = drive_service.permissions().list(fileId=file_id).execute()
    return permissions.get('items', [])
  except Exception as error:
    print('An error occurred: %s' % error)
  return None


def Get_All_Share_Links():
    Paths = []

    results = drive_service.files().list(fields="nextPageToken, files(id, name, webViewLink)").execute()
    items = results.get('files', [])
    for item in items:
        Link = {}
        Link['link'] = item['webViewLink']
        Link['name'] = item['name']
        Paths.append(Link)
    return Paths

def searchFile(size,query):
    results = drive_service.files().list(
    pageSize=size,fields="nextPageToken, files(id, name, kind, mimeType)",q=query).execute()
    items = results.get('files', [])
    if not items:
        print('No files found.')
    else:
        print('Files:')
        for item in items:
            print(item)
            print('{0} ({1})'.format(item['name'], item['id']))

def Fix_All_File_Permsions():
    for i in list_Files():
        file_id = i['id']

        def callback(request_id, response, exception):
            if exception:
                # Handle error
                print(exception)
            else:
                print("Permission Id: %s" % response.get('id'))

        batch = drive_service.new_batch_http_request(callback=callback)
        user_permission = {
            'type': 'anyone',
            'role': 'writer'
        }
        batch.add(drive_service.permissions().create(
            fileId=file_id,
            body=user_permission,
            fields='id',
        ))
        domain_permission = {
            'type': 'anyone',
            'role': 'writer'
        }
        batch.add(drive_service.permissions().create(
            fileId=file_id,
            body=domain_permission,
            fields='id',
        ))
        batch.execute()











