# Send an HTML email with an embedded image and a plain text message for
# email clients that don't want to display the HTML.

from email.mime.multipart import MIMEMultipart
from email.mime.text import MIMEText
from email.mime.image import MIMEImage

# Define these once; use them twice!
strFrom = 'ElanTesterTools@gmail.com'
strTo = 'kennyshay123@gmail.com'




recipients = ["kennyshay123@gmail.com",
          "elantestertools@gmail.com",
          "ken.shay@nortekcontrol.com"]



recipients = ["kennyshay123@gmail.com",
          "Ben.Bickell@nortekcontrol.com",
          "elantestertools@gmail.com",
          "ken.shay@nortekcontrol.com"]





# Create the root message and fill in the from, to, and subject headers
msgRoot = MIMEMultipart('related')
msgRoot['Subject'] = 'Daily Graph Automated Message Test'
msgRoot['From'] = strFrom
msgRoot['To'] = strTo
msgRoot.preamble = 'This is a multi-part message in MIME format.'

# Encapsulate the plain and HTML versions of the message body in an
# 'alternative' part, so message agents can decide which they want to display.
msgAlternative = MIMEMultipart('alternative')
msgRoot.attach(msgAlternative)

msgText = MIMEText('This is the alternative plain text message.')
msgAlternative.attach(msgText)

# We reference the image in the IMG SRC attribute by the ID we give it below
msgText = MIMEText('<img src="cid:image1"><br><img src="cid:image2"><br><img src="cid:image3"><br><img src="cid:image4"><br>', 'html')
msgAlternative.attach(msgText)
#############################################################################################################################
# This example assumes the image is in the current directory
Image_Path = r"C:\Users\ken.shay\Desktop\Test\FrankPC_Station.png"
fp = open(Image_Path, 'rb')
msgImage = MIMEImage(fp.read())
fp.close()
# Define the image's ID as referenced above
msgImage.add_header('Content-ID', '<image1>')
msgRoot.attach(msgImage)

