<?php


$files = array();
$dir = opendir('fails'); // open the cwd..also do an err check.
while(false != ($file = readdir($dir))) {
        if(($file != ".") and ($file != "..") and ($file != "index.php")) {
                $files[] = $file; // put in array.
        }   
}

natsort($files); // sort.

// print.
foreach($files as $file) {
	

	echo '<div class="row">';
		
		echo '<div class="column">';
			echo '<a href="fails/'.$file.'/fail.php">'.$file.'</a>';
		echo '</div>';	
		
		
		echo '<div class="column">';
			echo '<img src="fails/'.$file.'/template.png" width="100" height="50">';
		echo '</div>';

		echo '<div class="column">';
			echo '<img src="fails/'.$file.'/target.png" width="100" height="50">';
		echo '</div>';
		echo '<br>';
		

	echo '</div>';




}


?>





