#############################################################################EMAIL







def Send_Email(Subject = 'Alert',Text = 'None'):
    gmail_user = "elanautoalert@gmail.com"

    msgRoot = MIMEMultipart('related')
    msgRoot['Subject'] = Subject
    msgRoot['From'] = gmail_user
    msgRoot['To'] = gmail_user

    msgAlternative = MIMEMultipart('alternative')
    msgRoot.attach(msgAlternative)

    msgRoot.preamble = 'This is a multi-part message in MIME format.'
    msgText = MIMEText(Text, 'html')
    smtp = smtplib.SMTP()
    gmail_password = "CoreBrands123"
    server = smtplib.SMTP_SSL('smtp.gmail.com', 465)
    server.ehlo()
    server.login(gmail_user, gmail_password)
    server.sendmail(gmail_user,Email_Alert_Recipients_For_Graph_Failure,msgRoot.as_string())
    server.close()
    print('Done')

