
from paths import All_Scripts_Folder_Location as scripts_location
from yattag import Doc
import os
from yattag import indent

station_and_scripts = []
excluded = []


extensions = ('.py')  # extinctions want to search

for dname, dirs, files in os.walk(scripts_location):
    dirs[:] = [d for d in dirs if d not in excluded]
    for fname in files:
        if (fname.lower().endswith(extensions)):
            script = os.path.join(dname, fname)
            script = os.path.basename(script)
            station = dname.split('Scripts\\')[1]
            station_and_scripts.append({"station":station,"script":script,})



doc, tag, text = Doc().tagtext()
doc.asis('<!DOCTYPE HTML>')



with tag('html', lang=" en"):
    with tag('head'):
        doc.asis('<!-- basic.html -->')
        with tag('style'):
            text("""table, th, td {
  border: 1px solid black;
}""")


        with tag('title'):
            text('basic.html')
        doc.asis('<meta charset = "UTF-8" />')
        #doc.asis('<link rel="stylesheet" href="style.css">')
    with tag('body'):
                with tag('table'):
                    with tag('tr'):
                        with tag('td'):
                            text('Frank_SC100')
                with tag('table', cellspacing="5", cellpadding="5"):
                    for row_index in range(100):
                        text(str(row_index))
                        with tag('tr'):
                            for index in range(100):
                                index = str(index)
                                with tag('td'):
                                    text(index)



result = indent(doc.getvalue())

with open(r"C:\Users\ken.shay\Desktop\output.html",'w') as f:
    f.write(result)


