def copyanything(src, dst):
    try:
        shutil.copytree(src, dst)
    except OSError as exc: # python >2.5
        if exc.errno == errno.ENOTDIR:
            shutil.copy(src, dst)
        else: raise



def Yes_Or_No_Message(text,title):
    # ctypes messagebox return values
    MB_OK = 0
    MB_OKCANCEL = 1
    MB_YESNOCANCEL = 3
    MB_YESNO = 4
    IDOK = 0
    IDCANCEL = 2
    IDABORT = 3
    IDYES = 6
    IDNO = 7

    resp = ctypes.windll.user32.MessageBoxA(0, "", "", 1)
    if resp == MB_OKCANCEL:
        print(resp)
        print('Yes')
        return True
    else:
        print(resp)
        print('No')
        return False


def Mbox(title, text, style):
    return ctypes.windll.user32.MessageBoxW(0, text, title, style)