class QPushButton_Custom_Class(QPushButton):
    def __init__(self,text,path):
        super().__init__()
        textt = text.capitalize().replace('.exe','')
        self.setText(textt)
        self.path = path

    def Was_Clicked(self):
        try:
            os.startfile(self.path)
        except Exception as e:
            print(e)


