    def save_button_clicked(self):
        curdir = os.getcwd()


        old_script = self.ui.lineEdit_6.text()
        new_script = self.ui.lineEdit_3.text()
        old_interface = str(self.ui.comboBox.currentText())
        new_interface = str(self.ui.comboBox_2.currentText())
        print(old_script,new_script,old_interface,new_interface)
        os.chdir(self.Apps_Folder)
        old_interface_list = os.listdir(old_interface)
        old_interface_list = [x for x in old_interface_list if x.endswith('.png')]
        new_interface_list = os.listdir(new_interface)
        new_interface_list = [x for x in new_interface_list if x.endswith('.png')]
        for pic in old_interface_list:
            if pic not in new_interface:
                old_pic_path = old_interface + '\\' + pic
                new_pic_path = new_interface + '\\' + pic
                #print(pic_path)
                shutil.copy2(old_pic_path,new_pic_path)
















    def fill_combo_boxes(self):
         self.Apps_Folder = Variable_File_Getter.getVariableFromFile('Applications_Folder_Location')
         Lista = os.listdir(self.Apps_Folder)
         self.ui.comboBox.clear()
         self.ui.comboBox_2.clear()
         for i in Lista:
             self.ui.comboBox.addItem(i)
             self.ui.comboBox_2.addItem(i)


    def open_old_script(self):
        try:
            os.system('notepad.exe ' + self.old_script)
        except Exception as e:
            print(e)

    def open_new_script(self):
        try:
            os.system('notepad.exe ' + self.new_script)
        except Exception as e:
            print(e)


    def old_script_button_clicked(self):
            if self.active_file_name != None:


                Directory = os.path.basename(self.active_file_name)
            else:
                Directory = self.All_Script_Folder

            os.chdir(Directory)

            if 'Windows-7' not in self.get_windows_version():

                name = QFileDialog.getOpenFileName(parent=self,directory = Directory,caption=self.tr("Select a file"),filter=self.tr('Image files (*.py)'),)
            else:
                name = QFileDialog.getOpenFileName(None, 'Open File', Directory, '*', '*', QFileDialog.DontUseNativeDialog)
            if name != '':
                file_name = name[0]
                self.ui.lineEdit_6.setText(file_name)
                self.old_script = file_name

    def new_script_button_clicked(self):
        if self.active_file_name != None:
            Directory1 = os.path.basename(self.active_file_name)
            print("##################")
            print(Directory1)
        else:
            Directory1 = self.All_Script_Folder
        print('#####################')
        print(Directory1)
        os.chdir(Directory1)


        if 'Windows-7' not in self.get_windows_version():

            name = QFileDialog.getSaveFileName(parent=self, directory= Directory1,
                                               caption=self.tr("Select a file"), filter=self.tr('Image files (*.py)'), )
        else:
            name = QFileDialog.getSaveFileName(None, 'Open File',  Directory1, '*', '*',
                                               QFileDialog.DontUseNativeDialog)
        if name != '':
            file_name = name[0]
            self.ui.lineEdit_3.setText(file_name)
            self.new_script = file_name


