Templet_Image_File = r"C:\Settings\Last_Templet_Image.png"
class Image_Display_Window_Class(QWidget):

    def __init__(self):
        super().__init__()
        #self.ui = uic.loadUi(r"database_viewer.ui")
        self.ui = uic.loadUi(r"C:\ImageScripter_2\Lib\site-packages\Script_Writer\diplay_image_for_capture.ui")
        self.ui.setWindowTitle("Image Display For Capture")
        self.Create_Default_Interface()
        self.Create_Default_Image_Name()
        Width = GetSystemMetrics(0)
        Height = GetSystemMetrics(1)
        self.ui.setGeometry(0, 0, Width, Height)
        self.Load_image()
        self.ui.showMaximized()
        load_image_picture_changed_paths = [Templet_Image_File]
        self.picture_changed_watcher = QtCore.QFileSystemWatcher(load_image_picture_changed_paths)
        self.picture_changed_watcher.fileChanged.connect(self.Load_image)
        self.ui.actionRefresh.triggered.connect(self.get_new_image_and_load)
        self.ui.pushButton_4.clicked.connect(self.get_new_image_and_load)
        #self.ui.pushButton_3.clicked.connect(self.capture)




        ##############################################################################################

    def capture(self):
        try:
            import subprocess
            if self.Default_Inteface and self.Default_Image_Name:
                subprocess.Popen(r"C:\ImageScripter_2\python.exe C:\ImageScripter_2\Lib\site-packages\Script_Writer\snipping_tool.py " + self.Default_Inteface + ' ' + self.Default_Image_Name, shell=True)
            elif self.Default_Inteface and not self.Default_Image_Name:
                subprocess.Popen(r"C:\ImageScripter_2\python.exe C:\ImageScripter_2\Lib\site-packages\Script_Writer\snipping_tool.py " + self.Default_Inteface,shell=True)
            else:
                subprocess.Popen(r"C:\ImageScripter_2\python.exe C:\ImageScripter_2\Lib\site-packages\Script_Writer\snipping_tool.py",shell=True)




        except Exception as e:
            print(e)


    def get_new_image_and_load(self):
            try:
                if self.Default_Inteface != None:
                    if self.Default_Inteface == 'PC':
                        Main_Image_File = r"C:\Settings\Last_Templet_Image.png"
                        import PIL.ImageGrab
                        im = PIL.ImageGrab.grab()
                        im.save(Main_Image_File)
                    else:
                        IP = self.Default_Inteface.Get_IP()
                        if IP != None:
                            Pass_or_Fail = Ping_Notify_If_Fail(IP)
                            if Pass_or_Fail != False:
                                newimage = self.Default_Inteface._get_image_of_screen_()
                                Image_Manager.Update_Templet_With_Image_Path(newimage)
                elif self.Default_Inteface == None:
                    print('Default Device Is None')
            except Exception as e:
                print(e)
                print(522)



    def Create_Default_Interface(self):
        try:
            if sys.argv[1] == 'PC':
                self.Default_Inteface = 'PC'
            else:
                self.Default_Inteface = eval(sys.argv[1])
            print(self.Default_Inteface)
        except Exception as e:
            print('##################')
            print(e)
            print('##################')
            self.Default_Inteface = None

    def Create_Default_Image_Name(self):
        try:
            self.Default_Image_Name = sys.argv[2]
            print(self.Default_Image_Name)
        except Exception as e:
            print('########@@@#############')
            print(e)
            print('##################')
            self.Default_Image_Name = None
        if self.Default_Image_Name:
            self.ui.label_2.setText(self.Default_Image_Name)
            pixmap_of_templet = QPixmap(self.Default_Image_Name)
            self.ui.label_3.setPixmap(pixmap_of_templet)


    def Load_image(self):
        print('Load Image')
        pixmap_ = QPixmap(Templet_Image_File)
        self.ui.label.setPixmap(pixmap_)






