List_Of_Commands = ['Click','RightClick','ClickRepeatNewOffset','Wait','WaitVanish','Exists','WaitContinue','Max','Min','PressAndHold','ClickDouble','Hover','ClickXY','GetXY','RightClickType','RightClickTypeThenPress']
List_Of_Application_Commands = ['Close','getWindow','RestartHard','Min','Max','CloseAndClean','Start','PushButton','PushButton.Click','ListBox','ListBox.Select','EditBox','EditBox.SetText','ListView','ListView.Select','ComboBox','ComboBox.Select','RadioButton','RadioButton.Click','CheckBox','CheckBox.Click']


class Information_Getter_Class():
    def __init__(self):
        pass

    def get_list_of_completes(self):
        List_OF_Completes = []
        List_Of_Applications = os.listdir(Applications_Path)
        List_Of_Applications = [x for x in List_Of_Applications if x != '.png']
        for App in List_Of_Applications:
            try:
                List_OF_Completes.append(App)

                os.chdir(Applications_Path)
                os.chdir(App)
                List_Of_Images = os.listdir()
                List_Of_Images = [x for x in List_Of_Images if x.endswith('.png')]
                List_Of_Images = [x for x in List_Of_Images if '_' not in x]
                for image in List_Of_Images:
                    image = image.strip('.png')
                    List_OF_Completes.append(image)
            except Exception as e:
                print(e)
        for App in List_Of_Applications:
            for command in List_Of_Application_Commands:
                List_OF_Completes.append(command)
                #print(App + '.' + command)
        for command in List_Of_Commands:
            List_OF_Completes.append(command)



        Other_Completions = ['UPS','UPS.On','UPS.Off','UPS.AllOff','UPS.AllOn','UPS.SetIP','StayOn','GoHome','WakeUp']
        for comp in Other_Completions:
            List_OF_Completes.append(comp)
        return List_OF_Completes

    def get_list_of_Applications(self):
        List_OF_Completes = []
        List_Of_Applications = os.listdir(Applications_Path)
        List_Of_Applications = [str(x) for x in List_Of_Applications if x != '.png']
        return List_Of_Applications


    def get_full_image_path(self,app,image):
        image = image + '.png'
        curDur = os.getcwd()
        os.chdir(str(Applications_Path))
        os.chdir(str(app))
        path = os.path.abspath(image)
        os.chdir(curDur)
        return path

    def get_full_app_path(self,app):
        curDur = os.getcwd()
        os.chdir(str(Applications_Path))
        path = os.path.abspath(str(app))
        os.chdir(curDur)
        return path


    def get_full_applications_folder_path(self):
        #curDur = os.getcwd()
        #os.chdir(str(Applications_Path))
        path = os.path.abspath(str(Applications_Path))
        #os.chdir(curDur)
        return path




