class Example(QWidget):

    def __init__(self):
        super().__init__()

        self.initUI()

    def initUI(self):
        image = cv2.imread(r"C:\Settings\Last_Templet_Image.png")
        #cv2.circle(image, (970,545), 25, (0, 0, 255))
        xy = Variable_File_Getter.getVariableFromFile('last_x_y')
        List = xy.split(',')
        self.x1 = int(List[0])
        self.y1 = int(List[1])
        #img22 = cv2.cross(image, (self.x1,self.y1), 3, (0, 0, 255), 3)
        img22 = cv2.circle(image, (self.x1,self.y1), 10, (0, 0, 255), -1)
        #img22 = cv2.circle(image, (self.y1,self.x1,), 5, (0, 0, 255), 3)
        cv2.imwrite(r"C:\Settings\Last_Templet_Image_with_dot.png",img22)
        #cv2.imshow('Test image', image)
        #cv2.waitKey(0)
        #cv2.destroyAllWindows()
        self.img = QImage(r"C:\Settings\Last_Templet_Image_with_dot.png")
        pixmap = QPixmap(QPixmap.fromImage(self.img))
        self.img_label = QLabel()
        self.img_label.setPixmap(pixmap)
        self.img_label.mousePressEvent = self.getPixel
        hbox = QHBoxLayout(self)
        hbox.addWidget(self.img_label)
        self.setLayout(hbox)
        self.show()

    def do_calculations(self):
        x_offset = self.x2 - self.x1
        y_offset = self.y2 - self.y1

        print("xoffset = ", x_offset)
        print("yoffset = ", y_offset)
        print("xoffset=",x_offset,',',"yoffset=",y_offset)



    def getPixel(self, event):
        try:
            self.x2 = event.pos().x()
            self.y2 = event.pos().y()
            c = self.img.pixel(self.x2,self.y2)  # color code (integer): 3235912
            print(self.x2,self.y2)
            self.do_calculations()
            #return x, y
        except Exception as e:
            print(e)



