class Special(QWidget):
    def __init__(self,content,name):
        QWidget.__init__(self)
        self.content = content
        self.original_txt = content
        self.name = name
        self.EditBox = QLineEdit(content)
        Save = QPushButton('Save')
        Reset = QPushButton('Reset')
        namel = QLabel(name)
        Save.clicked.connect(self.save_was_clicked)
        Reset.clicked.connect(self.reset_was_clicked)
        hbox = QHBoxLayout()
        hbox.addStretch(1)
        hbox.addWidget(namel)
        hbox.addWidget(self.EditBox)
        hbox.addWidget(Save)
        hbox.addWidget(Reset)
        self.setLayout(hbox)

    def save_was_clicked(self):
        with open(self.name,'w') as f:
            f.write(str(self.EditBox.text()))
        #print(os.getcwd())
        #print(self.name)
        #print(self.EditBox.text())

    def reset_was_clicked(self):
        self.EditBox.setText(self.original_txt)




