from ImageScripter import *
from elan import *



def YesNoCombobox(button,yes_or_no,x = 200):
    button.RealClick(xoffset = x)
    if yes_or_no == 'yes':
        Configurator.Type('y')
        Configurator.Press('enter')       
    elif yes_or_no == 'no':
        Configurator.Type('n')
        Configurator.Press('enter')

def SelectLetterInCombobox(button,string_to_type,x = 200):
    button.RealClick(xoffset = x)
    Configurator.Type(string_to_type)
    Configurator.Press('enter')       
 
        
YesNoCombobox(Configurator.hasmotiondetect,'yes')



    