from elan import *
def turn_on_interface():
	Configurator.interface.Click()
	Sleep(1)
	Configurator.itp12viewer.Max()
	Sleep(1)
	Configurator.powerscheme.Click()
	Sleep(1)
	Configurator.ComboBox.Select(0,0)
	Sleep(1)
	Configurator.ComboBox.Select(1,0)
	Sleep(2)
	Configurator.apply.Click()
	Sleep(5)
	Configurator.itp12viewer.Min()
	Sleep(3)
	Configurator.itp8viewer.Max()
	Sleep(1)
	Configurator.powerscheme.Click()
	Sleep(1)
	Configurator.ComboBox.Select(0,0)
	Sleep(1)
	Configurator.ComboBox.Select(1,0)
	Sleep(2)
	Configurator.apply.Click()
	Sleep(5)
#turn_on_interface()	
def wake_up_interface():
	Configurator.eventmapper.Click()
	Sleep(1)
	#Configurator.eventmaps.RightClickTypeThenPress('a','enter')
	Configurator.eventmaps.RightClickType('a')
	Sleep(3)
	Add.PushButton.Click('OK')
	Press('enter')
	Configurator.commandevent.Click(xoffset = 1150, yoffset = 40)
	AddEvent.ListBox.Select(0,'Interface Device')
	Sleep(2)
	AddEvent.ListBox.Select(1,'ITP12')
	Sleep(2)
	AddEvent.ListBox.Select(2,'Jump To Homepage')
	Sleep(2)
	AddEvent.ComboBox.Select(0,0)
	Sleep(1)
	AddEvent.PushButton.Click('OK')
	Sleep(3)
	Configurator.commandevent.Click(xoffset = 1150, yoffset = 40)
	AddEvent.ListBox.Select(0,'Interface Device')
	Sleep(2)
	AddEvent.ListBox.Select(1,'ITP8')
	Sleep(2)
	AddEvent.ListBox.Select(2,'Jump To Homepage')
	Sleep(2)
	AddEvent.ComboBox.Select(0,0)
	Sleep(1)
	AddEvent.PushButton.Click('OK')
	Sleep(5)
	Configurator.testcommandsnow.Click()
	Sleep(3)
