from elan import *
Configurator.Start()
Configurator.messaging.Click()
Configurator.elansipserver.Click()
Configurator.ComboBox.Select(0,'Enrollment Open')
Configurator.apply.Click()
Configurator.enrollopenselected.Wait()
Configurator.elanvideodoorbell2.Wait()
Configurator.elanvideodoorbell2.Click()
Configurator.statusokgreen.Wait()
Configurator.lighting.Click()
Sleep(4)
Configurator.elanvideodoorbell2.Click()
Configurator.statusokgreen.Wait()
Configurator.video.Click()
Configurator.elanvideodoorbell2.Click()
Sleep(4)
Configurator.statusokgreen.Wait()
Configurator.CloseAndClean()