

from elan.functions import Get_Device_List_Simple,Diff
from elan import *


#Past_List = ['Jandy AquaLink RS', 'Pentair IntelliTouch, EasyTouch', 'Virtual Pool Controller']
Past_List = ['Jandy AquaLink RS', 'Pentair IntelliCenter', 'Pentair IntelliTouch, EasyTouch', 'Virtual Pool Controller']

One = Configurator.poolcontrol
Two = Configurator.poolcontrollers

Current_List = Get_Device_List_Simple(One,Two)


Configurator.system.Click()

if Current_List != Past_List:
    Difference = Diff(Past_List,Current_List)
    error = 'List Changed\n' + str(Difference)
    raise ValueError(error)

