tries = 0
for i in range(100):
    Itp8.security10items.Click()
    sleep(5)
    if Itp8.securityblank.Exists() == True:
        Itp8.securityhighlighted.Click()
        if tries >= 50:
            raise ValueError('Blank Security Page')
        else:
            tries += 1
            continue
    else:
        break

