from elan import *



Itp8.irrigation10items.Click()
Say("Looking to see if the irrigation system is connected")
Itp8.irrigationmiddle.Wait()
sleep(3)


if Itp8.greenbar.Exists(threshold = .98) == True:
    Say("The irrigation system appears to be connected")
else:
    raise ValueError('Irrigation is not working')

Itp8.manual.Click()
Itp8.flowerbedblacklawn.Wait()
Itp8.flowerbeds.Click()
Say("Im going to start watering the flower beds")
Itp8.irrigationstart.Click()
Itp8.flowerbedswatering.Wait()
Say("The flower beds are now being watered")
sleep(4)
Say("Im going to stop watering the flowers")
Itp8.stopirrigation.Click()
Itp8.irrigationallzonesoff.Wait()
Say("The flower beds are no longer being watered")
Itp8.runnothighlighted.Click()
Itp8.runwithgreen.Wait()


Itp8.shudder.Click()



print("Finished")