def getTime():
    now = datetime.now()
    now = str(now.strftime("%c"))
    print(now)
    now = now.replace('/','_')
    now = now.replace(':','-')
    now = now.replace(' ','__')
    now = now.split('__')[0]
    return now
    #return now.strftime('%I:%M:%S')


src = r"C:\Elan_Tools\Cloud_Drive"
print('name_of_file')
name_of_file = getTime()
try:
    shutil.copytree(src, name_of_file)
    print('Finished Backup of Cloud')
except WindowsError:
    print('Backup Already Completed Today')
    pass
    
