
from elan import *

Viewer.Start()
Viewer.photos10items.Click()
Viewer.photosmiddletop.Wait()
sleep(3)
if Viewer.nophotos.Exists() == True:
    Say("Something is wrong with Photo's")
    raise ValueError("Something is wrong with Photo's")
else:
    Say("Photo's look good")

Viewer.shudder.Click(yoffset = 100)
#Viewer.photoback.SetThreshold(.80)

for i in range(10):
    try:
        Viewer.photonext.Click(threshold=.92)
    except:
        Viewer.photoshomeicon.Click(xoffset = 115)
    sleep(1)
    Viewer.photoloading.WaitVanish()
for i in range(10):
    try:
        Viewer.photoback.Click(threshold=.92)
    except Exception as e:
        print("Exception -> " + str(e))
        Viewer.photoback2.Click(threshold=.92)
    sleep(1)
    Viewer.photoloading.WaitVanish()
Viewer.elantriangle.Click()
Viewer.shudder.Click()
Viewer.homeicon10items.Wait()

Viewer.CloseAndClean()