from elan import *Configurator.Reset(start=True)Configurator.Start()Configurator.discovery.Click()Configurator.elannvr.Click(xoffset = 450)Sleep(5)ConfigureDevice.nvrstaticbox.Click()Sleep(5)ConfigureDevice.Edit.SetText(1,'150')ConfigureDevice.PushButton.Click('Configure')HlConfig.success.Wait(seconds = 30)HlConfig.PushButton.Click('OK') HlConfig.Press('enter')Configurator.CloseAndClean()