from elan import *
Configurator.Reset(start=True)
Configurator.Start(password = '')
Configurator.media.Click()
Configurator.videodisplays.RightClickTypeThenPress('aa','enter')
ImportIr.Edit.SetText(0,'LG')
ImportIr.ComboBox.Select(0,'LG')
ImportIr.ComboBox.Select(1,'Code Group 2')
ImportIr.PushButton.Click('OK')
Sleep(5)
Configurator.ComboBox.Select(1,'IR Output 02')
Sleep(2)
Configurator.apply.Click()
Sleep(2)
Configurator.irsenderiroutput2.Wait(seconds = 20)
Configurator.system.Click()
Configurator.media.Click()
Sleep(5)
Configurator.videodisplays.Click(xoffset = -92,yoffset = -5)
Configurator.lgtv.Click()
Configurator.irsenderiroutput2.Wait(seconds = 20)
Configurator.CloseAndClean()