
from elan.functions import Get_Device_List_Simple,Diff
from elan import *
#Finished 3/6/19
Past_List = ['Clipsal Lighting (Ethernet)', 'Clipsal Lighting (RS-232)', 'Generic Serial', 'HDL Bus Pro (Ethernet)', 'Leviton Z-Wave RS232 Network', 'Lutron QS Network (Ethernet)', 'Lutron QS Network (RS-232)', 'MiOS Z-Wave Network', 'Rako Bridge (RA/RTC/WA/WTC-Bridge) (Ethernet)', 'Smart-BUS Lighting (Ethernet)', 'Standard Connection', 'Tapko SIM-KNX', 'Vantage InFusion Network', 'Vantage Q-Series Network']

Configurator.Reset(start=True)
Configurator.Start(password = '')


One = Configurator.lighting
Two = Configurator.communicationdevices

Current_List = Get_Device_List_Simple(One,Two)


Configurator.system.Click()

if Current_List != Past_List:
    Difference = Diff(Past_List,Current_List)
    error = 'List Changed\n' + str(Difference)
    raise ValueError(error)


Configurator.CloseAndClean()




