from elan import *
import random


#Viewer.Start(quick = True)
GetBackTOHomeScreenPC()

List_one = [Viewer.obf2,Viewer.odf2]
List_two = [Viewer.obv2,Viewer.odv2]
List_three = [Viewer.obf4,Viewer.odf4]
List_four = [Viewer.obv4,Viewer.odv4]

Master_List = [List_one,List_two,List_three,List_four]

Random_List = random.choice(Master_List)


for CAMERA in Random_List:
    GetBackTOHomeScreenPC()
    Viewer.video10items.Click()
    ##################################SCROLL TO TOP
    Viewer.videocameras.Click(xoffset = 420,yoffset = 70,threshold = .90,wait = False)
    Viewer.videocameras.Click(xoffset = 420,yoffset = 70,threshold = .90,wait = False)
    Viewer.videocameras.Click(xoffset = 420,yoffset = 70,threshold = .90,wait = False)
    #################################################################

    Viewer.videocameras.Click(xoffset = -200,yoffset = 100,threshold = .90,wait=False)

    #Viewer.ip8.Wait(threshold = .90)
    Viewer.tp12topmiddle.Wait(threshold = .90,wait=False)

    for i in range(100):
        if CAMERA.Exists(wait = False) == False:
            Viewer.gonextarrow.Click(wait = False)
            sleep(5)
        else:
            Viewer.dvrisavailable.Click()
            break
    Sleep(5)

    if Viewer.lotsofblue.Exists() == True:
        Sleep(3)
        if Viewer.lotsofblue.Exists() == True:
            raise ValueError("DVR Camera is not working")
    Viewer.shudder.Click(yoffset=460,xoffset=200)
    for i in range(5):#FAILED HERE NExt Slice
        Viewer.nextslice.Click()
        Sleep(2)
    Viewer.dvrplay.Click()
    Sleep(5)
    for i in range(3):
        Viewer.backslice.Click()
        Sleep(2)
        Viewer.backslice.Click()
        Sleep(2)
        Viewer.dvrplay.Click()
        Sleep(5)
    Viewer.dvrpause.Click()
    Sleep(2)
    Viewer.backback.Click(wait = False)
    Sleep(2)
    Viewer.eventlist.Click(wait = False)
    if Viewer.noevents.Exists() == True:
        Sleep(3)
        if Viewer.noevents.Exists() == True:
            raise ValueError("DVR Is Not Working")
    Sleep(2)
    for i in range(3):
        for i in range(3):
            Viewer.eventlisthighlighted.Click(yoffset=465, xoffset=55,wait=False)
            Viewer.lotsofgrey.WaitVanish(wait=False)
        Sleep(2)
        Viewer.motion.Click()
        Sleep(2)
        Viewer.play2.Click()
        Sleep(2)
        Viewer.back2.Click()
        Sleep(2)
        Viewer.pause2.Click()
        Sleep(2)
