from elan import *
Configurator.Start()
Configurator.climate.Click()
Configurator.elanstat.Click()
Configurator.Edit.SetText(0,'Thermostat')
Configurator.applythermostat.Click()
Sleep(5)
Configurator.elanstat2.Click()
Configurator.Edit.SetText(0,'Second Thermostat')
Configurator.applythermostat.Click()
Sleep(5)
Configurator.system.Click()
Configurator.climate.Click()
Configurator.elanstat22.Click()
Configurator.thermostat.Wait()
Configurator.elanstat23.Click()
Configurator.secondthermostatimage.Wait()
Configurator.CloseAndClean()
Configurator.Reset(start = True)