from elan import *
List = ['Aprilaire RS485 Network','Carrier CZII System','Carrier Infinity System','Cool Master RS232','Generic MODBUS','Generic Serial','HAI Stand-Alone Thermostat Network','Heatmiser','Leviton Z-Wave RS232 Network','RCS Thermostat Network','Standard Connection','Tapko SIM-KNX (RS232)','Tekmar RS232 Network','Virtual HVAC Network']
for i in List:
	print(i)
	Configurator.Start()
	Configurator.climate.Click()
	Configurator.communicationdevices.RightClickType('a')
	Sleep(4)
	Add.Type('@@@')
	Add.ListView.Select(0,i)
	Sleep(4)
	Add.ListBox.Select(0,'Serial Port')
	Sleep(5)
	Add.ComboBox.Select(0,0)
	Sleep(5)
	Add.PushButton.Click('OK')
	Configurator.atatat.Wait()
	Configurator.atatat.RightClickType('d')
	Sleep(3)
	HlConfig.PushButton.Click('Yes')
	Press('enter')
	Configurator.atatat.WaitVanish()
	#Configurator.HardReboot()
	Configurator.CloseAndClean()
