Configurator.CloseAndClean()
Viewer.Start()
Viewer.climate.Click()
if Viewer.offline.Exists() == True:
	raise ValueError('offline exists')
if Viewer.offline.Exists() == False:
	pass
Viewer.CloseAndClean()
Configurator.Start()
