from elan import *Configurator.Reset(start = True)Configurator.Start()Configurator.climate.Click()Configurator.thermostats.RightClickType('a')AddNewThermostat.ListView.Select(1,"ELAN WiFi Thermostat")AddNewThermostat.PushButton.Click('OK')Sleep(5)Configurator.Edit.SetText(7,'10')Configurator.applythermostat.Click()Sleep(2)Configurator.CloseAndClean()Viewer.StartViewer.climate.Click()if Viewer.offline.Exists() == True:	raise ValueError('offline exists')if Viewer.offline.Exists() == False:	passViewer.CloseAndClean()