from elan import *
#DONE
Viewer.Start()
GetBackTOHomeScreenPC()
Viewer.climate.Click()
Viewer.climatemiddletop.Wait()
sleep(3)
if Viewer.climateoffline.Exists() == True:
    raise ValueError("Exception 7, a Thermostat appears to be offline!")
else:
    pass
##############################################################
d = 0
for x in range(100):
    try:
        Viewer.righttermostatcustompage.Click()
        sleep(3)
        break
    except:
        d += 1
        if d >= 60:
            raise ValueError('Something went wrong with climate')
        Viewer.climatehighlighted.Click()
        sleep(5)
        Viewer.climate.Click()
        sleep(5)
#########################################################################
            
            
            
Viewer.settocoolmode.Click()
sleep(2)
Viewer.setcoolto99.Click()
Viewer.setfantoauto.Click()
Viewer.shudder.Click()
Viewer.cool99updown.Wait()
Viewer.rightthermostat.Click()
Viewer.bigcoolsetto99.Wait(threshold = .92)
Viewer.shudder.Click()
Viewer.climatehighlighted.Click()

Viewer.CloseAndClean()



print("Finished")