import time


def Sleep(x,noprint = False):
    if type(x) != float:
        timeAmount = int(x)
        for t in range(timeAmount):
            time.sleep(1)
            if timeAmount >= 1:
                if noprint == False:
                    print('Zzz ' + str(timeAmount) + ' zzZ')
            timeAmount -= 1
    elif type(x) == float:
        if x > 2:
            if noprint == False:
                print('ZzzzzZ')
        time.sleep(x)

def say(x):
    print(x)

def Say(x):
    say(x)

