import tkinter as tk

class Error_Window_Self_Destruct_Class():
    def __init__(self,Text):
        import tkinter as tk
        root = tk.Tk()
        root.attributes('-topmost', 'true')
        root.attributes("-fullscreen", True)
        root.configure(background='black')
        root.after(10000, lambda: root.destroy())  # Destroy the widget after 10 seconds
        #root.after(2500, lambda: root.destroy())
        L = tk.Label(root, text=Text)
        L.pack()
        root.mainloop()

if __name__ == "__main__":
    Error_Window = Error_Window_Self_Destruct_Class('test')

