import os, shutil
import pyautogui

def get_data_from_file(x):
    with open(x, 'r') as f:
        data = f.readline()
    ###For Bool
    if data == "True":
        return True
    elif data == "False":
        return False
    else:
        return data

def Press(stringToPress):
    print("Pressing -> ", stringToPress)
    pyautogui.press(stringToPress)
    print("Pressed -> ", stringToPress)

