import os
from shutil import copyfile
from paths import *


##Settings_Location = r"C:\Settings"
from PIL import Image

##Target_Path = r"C:\Settings\Last_Target_Image.png"
##Template_Path = r"C:\Settings\Last_Templet_Image.png"


class Image_Manager_Class():
    def __init__(self):
        pass
    def erase_target(self):
        os.remove(Target_Path)
    def erase_template(self):
        os.remove(Template_Path)
    def erase_both(self):
        self.erase_target()
        self.erase_template()

    def Update_Templet_With_Image_Path(self,image_path):
        copyfile(image_path,Template_Path)









    def replace_both_with_black(self):
        try:
            #im = Image.open(Target_Path)
            im = Image.open(Template_Path)
            target_width, target_height = im.size
            im.close()
            Target = Image.new('RGB', (target_height, target_width), (0, 0, 0))
            Target.save(Target_Path, "PNG")
            im = Image.open(Template_Path)
            template_width, template_height = im.size
            im.close()
            Template = Image.new('RGB', (template_height, template_width), (0, 0, 0))
            Template.save(Template_Path, "PNG")
        except Exception as e:
            print(e)











Image_Manager = Image_Manager_Class()


if __name__ == "__main__":
    Image_Manager.replace_both_with_black()



