class ITP12_Class(Device_Class):
    def _click_(self,x,y):
        print("_click_")
        x = str(x)
        y = str(y)
        nbytes = 4096
        command = """#!/bin/sh
        tp_testing=$(find /sys/ -name tp_testing)
        echo "0 #X #Y 1" >$tp_testing
        echo "0 #X #Y 0" >$tp_testing
        sleep 1
        """
        command = command.replace('#X', x)
        command = command.replace('#Y', y)
        def createSSHClient():
            client = paramiko.SSHClient()
            client.load_system_host_keys()
            client.set_missing_host_key_policy(paramiko.AutoAddPolicy())
            client.connect(self._ipadress_, self._port_, self._username_, self._password_)
            return client
        client = paramiko.Transport((self._ipadress_, self._port_))
        client.connect(username=self._username_, password=self._password_)
        stdout_data = []
        stderr_data = []
        session = client.open_channel(kind='session')
        session.exec_command(command)
        while True:
            if session.recv_ready():
                stdout_data.append(session.recv(nbytes))
            if session.recv_stderr_ready():
                stderr_data.append(session.recv_stderr(nbytes))
            if session.exit_status_ready():
                break
        session.close()
        client.close()
        print("_click_")

    def setIP(self,ip):
        self._ipadress_ = ip
        F = r"C:\ImageScripter_2\Lib\site-packages\elan\Applications_And_Devices\Itp12\ip_address.txt"
        with open(F,'w') as f:
            f.write(ip)

    def _get_image_of_screen_(self):
        def createSSHClient():
            client = paramiko.SSHClient()
            client.load_system_host_keys()
            client.set_missing_host_key_policy(paramiko.AutoAddPolicy())
            client.connect(self._ipadress_, self._port_, self._username_, self._password_)
            return client
        for atime in range(100): #Should run only once, unless failure
            try:
                nbytes = 4096
                command = 'scrot --thumb 50 /tmp/test.png'
                client = paramiko.Transport((self._ipadress_, self._port_))
                client.connect(username=self._username_, password=self._password_)
                stdout_data = []
                stderr_data = []
                session = client.open_channel(kind='session')
                session.exec_command(command)
                while True:
                    if session.recv_ready():
                        stdout_data.append(session.recv(nbytes))
                    if session.recv_stderr_ready():
                        stderr_data.append(session.recv_stderr(nbytes))
                    if session.exit_status_ready():
                        break
                #print 'exit status: ', session.recv_exit_status()
                #print ''.join(stdout_data)
                #print ''.join(stderr_data)
                session.close()
                client.close()
                ssh = createSSHClient()
                scp = SCPClient(ssh.get_transport())
                scp.get('/tmp/test.png', screenshot_file)
                break
            except PermissionError as e:
                print(e)
        return screenshot_file


