from elan import *

import os
Cloud_Path = r"C:\Elan_Tools\Cloud_Drive"

def get_size(start_path = '.'):
    total_size = 0
    for dirpath, dirnames, filenames in os.walk(start_path):
        for f in filenames:
            fp = os.path.join(dirpath, f)
            total_size += os.path.getsize(fp)
    return total_size

print(get_size(Cloud_Path))







