from imagescripter import *
try:
	Configurator.video.Click()
	Sleep(3)
	for camera in [Configurator.odf22134,Configurator.elnvr8ch,Configurator.elipdodv2bk]:
		camera.Click()
		Configurator.name12313214124.Wait()
		sleep(3)
		if Configurator.firmwareupdateavailable.Exists():
			#print("$$$$$$$$$$$")
			raise ValueError('New Device Firmware might Exists')
		else:
			try:
				Configurator.firmwareisok.Wait(seconds=20)
			except:
				raise ValueError('New Device Firmware might Exists, ok was not found')
	Configurator.system.Click()	
	Configurator.CloseAndClean()
except:
	try:
		Configurator.CloseAndClean()
	except:
		pass
	raise