import os


SettingsFolder = r"C:\Settings"

class Variable_File_Getter_Class():
    def __init__(self):
        pass
    def getVariableFromFile(self,filename):
        print('Get Var From File')
        curdur = os.getcwd()
        os.chdir(SettingsFolder)
        with open(filename + '.txt','r') as f:
            var = f.read()
        os.chdir(curdur)
        print(var)
        return var
    def setFileFromVariable(self,filename,variable):
        print('Set File From Var')
        curdur = os.getcwd()
        os.chdir(SettingsFolder)
        with open(filename + '.txt','w') as f:
            f.write(variable)
        os.chdir(curdur)


Variable_File_Getter = Variable_File_Getter_Class()


if __name__ == "__main__":
    controller_password = Variable_File_Getter.getVariableFromFile("Controller_Name")

