import os


SettingsFolder = r"C:\Settings"

class Variable_File_Getter_Class():
    def __init__(self):
        pass
    def getVariableFromFile(self,filename):
        if not filename.endswith('.txt'):
            filename = filename + '.txt'
        curdur = os.getcwd()
        os.chdir(SettingsFolder)
        if not os.path.isfile(filename):
            raise ValueError("File Does Not Exist")
        else:
            pass
        with open(filename,'r') as f:
            var = f.read()
        os.chdir(curdur)
        var = var.replace('\n','')
        if var == '':
            raise ValueError('File is blank')
        return var
    def setFileFromVariable(self,filename,variable):
        if not filename.endswith('.txt'):
            filename = filename + '.txt'
        #print('Set File From Var')
        curdur = os.getcwd()
        os.chdir(SettingsFolder)
        if not os.path.isfile(filename):
            path = SettingsFolder + '\\' + filename
            print('################')
            print(path)
            with open(path,'w') as f:
                f.write(variable)
        else:
            pass
        with open(filename,'w') as f:
            f.write(variable)
        os.chdir(curdur)


Variable_File_Getter = Variable_File_Getter_Class()


if __name__ == "__main__":
    controller_password = Variable_File_Getter.getVariableFromFile("Controller_Name")

